import "../Style/header.css"
import $ from 'jquery';

let scrollTop = 0;
window.addEventListener("scroll", function () {
    let st = window || document.documentElement.scrollTop;
    if (st > scrollTop) {
        // scroll down
        $('.operative1').addClass('operativeSelected');

    } else if (st < scrollTop) {
        // scroll up
        $('nav').css({
            Animation: `navDownAnim .5s ease 0s 1 reverse forwards`
        })
    }
    scrollTop = st <= 0 ? 0 : st; // For Mobile or negative scrolling
}, false);


function header() {
    return (
        <nav>
            <div id="headerLogo"></div>
            <div>
                <ul id="navLinks">
                    <p className="nav-link">ABOUT</p>
                    <p className="nav-link">GALLERY</p>
                    <p className="nav-link">CONTACT</p>
                    <p className="nav-link">QUOTE</p>
                </ul>
            </div>
        </nav>
    );
}

export default header;
