import React, { useState, useEffect } from "react";
import "../Style/contact.css"

const FORM_ENDPOINT = "https://public.herotofu.com/v1/6e969c80-2d4d-11ee-9314-312e17da8d1e"; // TODO - fill on the later step

const ContactForm = () => {


    const defaultProps = {
        center: {
          lat: 10.99835602,
          lng: 77.01502627
        },
        zoom: 11
      };





    const [submitted, setSubmitted] = useState(false);
    const handleSubmit = () => {
        setTimeout(() => {
            setSubmitted(true);
        }, 100);
    };

    if (submitted) {
        return (
            <div id="contactArea" className="section">
                <div className="alignTop">
                    <div id="contactText" className="Text">
                        <h1 id="contactTitle">Give me a<br />shout<span>.</span></h1>
                        <p>I am interested in a full-time frontend development or web design role but am equally keen for freelance opportunities. Whatever it is - please get in contact.</p>
                    </div>
                    <div id="contactForm">
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div id="contactArea">

            <div id="contactText">
                <h1>DROP US A LINE</h1>
            </div>

            <div id="contactForm">
                <form
                    action={FORM_ENDPOINT}
                    onSubmit={handleSubmit}
                    method="POST"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    <div>
                        <input
                            type="text"
                            placeholder="Name"
                            name="name"
                            className="inputField"
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="email"
                            placeholder="Email"
                            name="email"
                            className="inputField"
                            required
                        />
                    </div>
                    <div>
                        <textarea
                            placeholder="Message"
                            name="message"
                            id="messageArea"
                            className="inputField"
                            required
                        />
                    </div>
                    <div>
                        <button id="contactButton" type="submit">
                            SEND A MESSAGE
                        </button>
                    </div>
                </form>
            </div>


            <div id="contactText">
                <h2>BETTER YET, SEE US IN PERSON!</h2>
                <p>(Please call before coming to confirm we're there)</p>
                <p>The Sanderson Centre, Gosport, PO12 3UL, United Kingdom</p>
            </div>
            <div style={{ height: '100vh', width: '100%' }}>
      <GoogleMapReact
        bootstrapURLKeys={{ key: "" }}
        defaultCenter={defaultProps.center}
        defaultZoom={defaultProps.zoom}
      >
        <AnyReactComponent
          lat={59.955413}
          lng={30.337844}
          text="My Marker"
        />
      </GoogleMapReact>
    </div>
        </div>
    );
};

export default ContactForm;
