import React, { useState } from 'react';
import '../Style/HamburgerMenu.css';

const HamburgerMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleMenuToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleMenuItemClick = () => {
    setIsOpen(false); // Close the menu when an item is clicked
  };

  return (
    <div className={`hamburger-menu ${isOpen ? 'open' : ''}`}>
      <div className="hamburger-icon" onClick={handleMenuToggle}>
        <div className="hamburger-line"></div>
        <div className="hamburger-line"></div>
        <div className="hamburger-line"></div>
      </div>
      {isOpen && (
        <ul className="menu-items">
          <li onClick={handleMenuItemClick}>Item 1</li>
          <li onClick={handleMenuItemClick}>Item 2</li>
          <li onClick={handleMenuItemClick}>Item 3</li>
        </ul>
      )}
    </div>
  );
};

export default HamburgerMenu;
