import "../Style/hero.css"
import video from '../Images/VideoLarger.mp4';
import React, { useEffect } from 'react';

function hero() {




    useEffect(() => {
        window.addEventListener("scroll", onScroll);
        function onScroll() {
            const scrollTop = $(window).scrollTop();
            const heroContentOffset = document.getElementById('heroTitle').offsetTop;

            $('#hero').css({
                opacity: ((heroContentOffset * 1.75) - scrollTop) / heroContentOffset
            })
            $('#heroContent').css({
                opacity: (heroContentOffset - scrollTop) / heroContentOffset
            })
            $('#backgroundFill').css({
                opacity: ((heroContentOffset * 2) - scrollTop) / heroContentOffset
            })
        }
    }, []);






    return (
        <div className="video-container">
            <div id="overlay"></div>
            <video autoPlay muted loop playsInline poster="Images/BuffaloLogo.svg">
                <source src={video} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
            <div id="callToAction">FROM CONCEPT TO FINISHED PRODUCT</div>
        </div>
    );
}

export default hero;
