import "../Style/Testimonial.css"
import React, { useState, useEffect } from 'react';



function Testimonial() {
  const headingTexts = ['poop 1', 'Heading 2', 'Heading 3'];
  const paragraphTexts = [
    'This is paragraph 1.',
    'This is paragraph 2.',
    'This is paragraph 3.',
  ];
  const authorTexts = ['Author 1', 'Author 2', 'Author 3'];

  const [index, setIndex] = useState(0);
  const [heading, setHeading] = useState(headingTexts[0]);
  const [paragraph, setParagraph] = useState(paragraphTexts[0]);
  const [author, setAuthor] = useState(authorTexts[0]);

  useEffect(() => {
    // Function to cycle through the text with fading effect
    const cycleText = () => {
      const nextIndex = (index + 1) % headingTexts.length;
      setIndex(nextIndex);

      // Set the new texts with a brief delay to allow the fading effect to be seen
      setTimeout(() => {
        setHeading(headingTexts[nextIndex]);
        setParagraph(paragraphTexts[nextIndex]);
        setAuthor(authorTexts[nextIndex]);
      }, 1000); // Adjust the delay time as needed for a smooth transition
    };

    // Set an interval to call the cycleText function every 3 seconds (adjust the time as needed)
    const interval = setInterval(cycleText, 3000);

    // Clean up the interval when the component is unmounted
    return () => clearInterval(interval);
  }, [index, headingTexts, paragraphTexts, authorTexts]);








  return (
    <div id="testimonialSection">

      <div id="testimonialText">
        <div className="testimonialsTopBottom"><h1></h1></div>

        <div className="quoteIcon">
          <div className="testimonialsStartQuote"></div>
          <div className="spacer"></div>
        </div>

        <div className="testimonialQuote">
          <h3 style={{ transition: 'opacity 0.2s ease' }}>{heading}</h3>
          <p>{paragraph}</p>
          <div className="author">{author}</div>
        </div>

        <div className="quoteIcon">
          <div className="spacer"></div>
          <div className="testimonialsEndQuote"></div>
        </div>

        <div className="testimonialsTopBottom"></div>
      </div>
      <div id="testimonialLogo"></div>

    </div>
  );
}

export default Testimonial;
