import "../Style/intro.css"
import $ from 'jquery';

function Intro() {

    const handleMouseEnter1 = () => {
        $('#card1Title').css({
            height: `15vw`
        })
        $('#card1Title').find('p').css({
            opacity: `1`
        })
        $('.serviceButton').css({
            opacity: `1`
        })
    };

    const handleMouseLeave1 = () => {
        $('#card1Title').css({
            height: `2vw`
        })
        $('#card1Title').find('p').css({
            opacity: `0`
        })
        $('.serviceButton').css({
            opacity: `0`
        })
    };

    const handleMouseEnter2 = () => {
        $('#card2Title').css({
            height: `15vw`
        })
        $('#card2Title').find('p').css({
            opacity: `1`
        })
        $('.serviceButton').css({
            opacity: `1`
        })
    };

    const handleMouseLeave2 = () => {
        $('#card2Title').css({
            height: `2vw`
        })
        $('#card2Title').find('p').css({
            opacity: `0`
        })
        $('.serviceButton').css({
            opacity: `0`
        })
    };


    $('#callToAction').css({
        opacity: 1,
        display: `flex`
    })



    return (
        <div id="intro">
            <h1>PRECISION FORMED SHEET METAL FABRICATIONS
                <br />AND MACHINED COMPONENTS, UK AND WORLDWIDE</h1>
            <p>MJ Sections Ltd represents over 40 years of engineering innovation, we are a global market leader working in partnership across multiple sectors.
                <br /><br />
                A worldwide leader in precision formed sheet metal fabrications and machined components, by providing a timely and pro-active response to the needs of its customers, and ensuring that over and above contractual requirements, all products are fit for purpose and embody the highest standards of workmanship.</p>
            <div id="services">

                <div id="serviceCard1" className="serviceCard" onMouseEnter={handleMouseEnter1} onMouseLeave={handleMouseLeave1}>
                    <div id="card1Title">
                        <h2>METAL FABRICATION</h2>
                        <p>Bunch of text</p>
                        <div className="serviceButton">More</div>
                    </div>
                </div>


                <div id="serviceCard2" className="serviceCard" onMouseEnter={handleMouseEnter2} onMouseLeave={handleMouseLeave2}>
                    <div id="card2Title">
                        <h2>METAL FABRICATION</h2>
                        <p>Bunch of text</p>
                        <div className="serviceButton">More</div>
                    </div>
                </div>


                <div id="serviceCard3" className="serviceCard" onMouseEnter={handleMouseEnter3} onMouseLeave={handleMouseLeave3}>
                    <div id="card1Title">
                        <h2>METAL FABRICATION</h2>
                        <p>Bunch of text</p>
                        <div className="serviceButton">More</div>
                    </div>
                </div>


                <div id="serviceCard2" className="serviceCard" onMouseEnter={handleMouseEnter2} onMouseLeave={handleMouseLeave2}>
                    <div id="card2Title">
                        <h2>METAL FABRICATION</h2>
                        <p>Bunch of text</p>
                        <div className="serviceButton">More</div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Intro;
