import "../Style/Testimonial.css"
import React, { useState, useEffect } from 'react';



function Testimonial() {
  const headingTexts = ['Heading 1', 'Heading 2', 'Heading 3'];
  const paragraphTexts = [
    'This is paragraph 1.',
    'This is paragraph 2.',
    'This is paragraph 3.',
  ];
  const authorTexts = ['Author 1', 'Author 2', 'Author 3'];

  const [index, setIndex] = useState(0);

  useEffect(() => {
    // Function to cycle through the text with fading effect
    const cycleText = () => {
      const nextIndex = (index + 1) % headingTexts.length;
      setIndex(nextIndex);
    };

    // Set an interval to call the cycleText function every 3 seconds (adjust the time as needed)
    const interval = setInterval(cycleText, 3000);

    // Clean up the interval when the component is unmounted
    return () => clearInterval(interval);
  }, [index, headingTexts.length]);








  return (
    <div id="testimonialSection">

      <div id="testimonialText">
        <div className="testimonialsTopBottom"><h1></h1></div>

        <div className="quoteIcon">
          <div className="testimonialsStartQuote"></div>
          <div className="spacer"></div>
        </div>

        <div className="testimonialQuote">
          <h3 className={index === 0 ? 'visible' : 'hidden'}>{heading}</h3>
          <p className={index === 0 ? 'visible' : 'hidden'}>{paragraph}</p>
          <p className={index === 0 ? 'visible' : 'hidden'} style={{ fontStyle: 'italic' }}>{author}</p>

          <h3 className={index === 1 ? 'visible' : 'hidden'}>{heading}</h3>
          <p className={index === 1 ? 'visible' : 'hidden'}>{paragraph}</p>
          <p className={index === 1 ? 'visible' : 'hidden'} style={{ fontStyle: 'italic' }}>{author}</p>

          <h3 className={index === 2 ? 'visible' : 'hidden'}>{heading}</h3>
          <p className={index === 2 ? 'visible' : 'hidden'}>{paragraph}</p>
          <p className={index === 2 ? 'visible' : 'hidden'} style={{ fontStyle: 'italic' }}>{author}</p>
        </div>

        <div className="quoteIcon">
          <div className="spacer"></div>
          <div className="testimonialsEndQuote"></div>
        </div>

        <div className="testimonialsTopBottom"></div>
      </div>
      <div id="testimonialLogo"></div>

    </div>
  );
}

export default Testimonial;
